          ];

          // Loop over the arrays to style the cells
          cellsToStyle.forEach(function (group) {
              group.positions.forEach(function (cell) {
                  styleCell_rah7m7dco1wng1ba5hnq(cell.i, cell.j, group.css_id);
              });
          });
      });
