```{=typst}
#show figure: set block(breakable: true)

#block[ // start block

  #let style-dict = (
    // tinytable style-dict after
  )

  #let style-array = ( 
    // tinytable cell style after
  )

  // Helper function to get cell style
  #let get-style(x, y) = {
    let key = str(y) + "_" + str(x)
    if key in style-dict { style-array.at(style-dict.at(key)) } else { none }
  }

  // tinytable align-default-array before
  #let align-default-array = ( left, left, left, left, left, left, left, left, left, left, left, ) // tinytable align-default-array here
  #show table.cell: it => {
    if style-array.len() == 0 { return it }

    let style = get-style(it.x, it.y)
    if style == none { return it }

    let tmp = it
    if ("fontsize" in style) { tmp = text(size: style.fontsize, tmp) }
    if ("color" in style) { tmp = text(fill: style.color, tmp) }
    if ("indent" in style) { tmp = pad(left: style.indent, tmp) }
    if ("underline" in style) { tmp = underline(tmp) }
    if ("italic" in style) { tmp = emph(tmp) }
    if ("bold" in style) { tmp = strong(tmp) }
    if ("mono" in style) { tmp = math.mono(tmp) }
    if ("strikeout" in style) { tmp = strike(tmp) }
    if ("smallcaps" in style) { tmp = smallcaps(tmp) }
    tmp
  }

  #align(center, [

  #table( // tinytable table start
    columns: (auto, auto, auto, auto, auto, auto, auto, auto, auto, auto, auto),
    stroke: none,
    rows: auto,
    align: (x, y) => {
      let style = get-style(x, y)
      if style != none and "align" in style { style.align } else { left }
    },
    fill: (x, y) => {
      let style = get-style(x, y)
      if style != none and "background" in style { style.background }
    },
 table.hline(y: 1, start: 0, end: 11, stroke: 0.05em + black),
 table.hline(y: 7, start: 0, end: 11, stroke: 0.1em + black),
 table.hline(y: 0, start: 0, end: 11, stroke: 0.1em + black),
    // tinytable lines before

    // tinytable header start
    table.header(
      repeat: true,
[mpg], [cyl], [disp], [hp], [drat], [wt], [qsec], [vs], [am], [gear], [carb],
    ),
    // tinytable header end

    // tinytable cell content after
[21.0], [6], [160], [110], [3.90], [2.620], [16.46], [0], [1], [4], [4],
[21.0], [6], [160], [110], [3.90], [2.875], [17.02], [0], [1], [4], [4],
[22.8], [4], [108], [93], [3.85], [2.320], [18.61], [1], [1], [4], [1],
[21.4], [6], [258], [110], [3.08], [3.215], [19.44], [1], [0], [3], [1],
[18.7], [8], [360], [175], [3.15], [3.440], [17.02], [0], [0], [3], [2],
[18.1], [6], [225], [105], [2.76], [3.460], [20.22], [1], [0], [3], [1],

    // tinytable footer after

    table.footer(
      repeat: false,
      // tinytable notes after
